import React from 'react';

//отвечает за получение и отрисовку ошибок на sign in / signUp
const BackendErrorMessages = ({backendError}) => {
  // console.log('backendErrors', backendError)

  //Проходим по ключам backedError, вытаскиваем каждый name через пробел и конкатенируем
  const errorMessages = Object.keys(backendError).map(name => {
    const messages = backendError[name].join(' ')
    //<li key={name}>{`${name} ${messages}`}</li>
    return `${name} ${messages}`
  })
  // console.log(errorMessages)
  return (
    <ul className='error-messages'>
      {errorMessages.map(errorMessages => (
        <li key={errorMessages}>{errorMessages}</li>
      ))}
    </ul>
  );
};

export default BackendErrorMessages;
