import React, { createContext, useState } from 'react'

//отвечает за назначение контекста

//указываем дефолтное значение как у хука, value {} и setter в качестве функции
//на вход передаем массив и с двумя элементами: объектом и функцией
const CurrentUserContext = createContext([{}, () => {}])

const CurrentUserProvider = ({ children }) => {
  const [state, setState] = useState({
    isLoading: false,
    isLoggedIn: null,
    currentUser: null,
  })
  return (
    //обертка для всех компонентов которым понадобитьсяд доступ к контексту
    <CurrentUserContext.Provider value={[state, setState]}>{children}</CurrentUserContext.Provider>
  )
}

export { CurrentUserContext, CurrentUserProvider }
