import { useState, useEffect, useCallback } from "react"
import axios from 'axios'
import useLocalStorage from 'hooks/useLocalStorage'
//отвечает за post и get запросы к api


export default url => {
  const _baseUrl = 'https://conduit.productionready.io/api',
        [isLoading, setIsLoading] = useState(false),
        [error, setError] = useState(null),
        [response, setResponse] = useState(null),
        [options, setOptions] = useState({}),
        [token] = useLocalStorage('token');

  // console.log('token', token)

  //Если options не указан, то будет посылаться get запрос по указанному url
  const doFetch = useCallback((options = {}) => {
    setOptions(options)
    setIsLoading(true)
  }, [])


  useEffect( () => {
    let skipGetResponseAfterUnmointing = false
    /*
      добавляем наш токен после сета options(headers), но перед их отправкой
      мерджим старые опции с новыми, внутри которых передаем токен или пустую строку в том случае
      если пользователь не залогинен
    */
    const requestOptions = {
      ...options,
      ...{
        headers: {
          authorization: token ? `Token ${token}` : ''
        }
      }
    }
    if (!isLoading) {
      return
    }

    axios(_baseUrl + url, requestOptions)
      .then(res => {
        if (!skipGetResponseAfterUnmointing) {
          setIsLoading(false)
          setResponse(res.data)
        }
      })
      .catch(error => {
        if (!skipGetResponseAfterUnmointing) {
          setIsLoading(false)
          setError(error.response.data)
        }
      })
    return () => {
      skipGetResponseAfterUnmointing = true
    }
  }, [isLoading, options, url, error, token])

  return[{isLoading, response, error}, doFetch]
}
