import {parse} from 'query-string'

//чтобы не импортировать lodash ради одного метода range
export const range  = (start, end) => {
  return [...Array(end).keys()].map(element => element + start)
}

//парсинг query string

export const limit = 10;

//передаем search из location
export const getPaginator = search => {
  const parsedSearch = parse(search)//object
  //приводит parsedSearch.page к number(т.к там в объекте строка)
  //если там пусто,то тогда возвращем 1 страницу
  const currentPage = parsedSearch.page ? Number(parsedSearch.page) : 1
  //формула расчета offset
  const offset = currentPage * 10 - limit

  return {currentPage, offset}
}


